#!/bin/bashif [ -x "$(command -v vcpkg)" ]; then	vcpkg install openssl	exit 0fiif [ -x "$(command -v apt-get)" ]; then	sudo apt-get install libssl-dev	exit 0fiif [ -x "$(command -v brew)" ]; then	sudo brew install openssl@1.1 && ln -s /usr/local/opt/openssl@1.1/include/openssl /usr/local/include && ln -s /usr/local/opt/openssl@1.1/lib/*.dylib /usr/local/lib && ln -s /usr/local/opt/openssl@1.1/lib/*.a /usr/local/lib	exit 0fiecho "install cannot be done automatically"exit 1